DROP TABLE IF EXISTS cliente;

CREATE TABLE cliente (
  id INT AUTO_INCREMENT  PRIMARY KEY,
  cpf_cnpj VARCHAR(250),
  agencia VARCHAR(6),
  conta VARCHAR(10),
  primeiro_nome VARCHAR(250),
  nome_completo VARCHAR(250),
  dt_nasc VARCHAR(10),
  telefone VARCHAR(250),
  celular VARCHAR(250),
  segmento VARCHAR(250)
);

